CREATE TABLE IF NOT EXISTS animals (id SERIAL PRIMARY KEY, "name" VARCHAR(255) NOT NULL, age INT NULL, time_unit VARCHAR(255) NULL, specie VARCHAR(255) NULL, description VARCHAR(255) NOT NULL, creation_date TIMESTAMP NULL, modification_date TIMESTAMP NULL, status VARCHAR(255) NOT NULL, sex VARCHAR(255) NOT NULL, "size" VARCHAR(255) NOT NULL, castrated BOOLEAN NOT NULL, created_by_id INT NOT NULL, CONSTRAINT fk_animals_created_by_id_id FOREIGN KEY (created_by_id) REFERENCES users(id) ON UPDATE RESTRICT)